import "./App.css";
import React, { Component } from "react";
import Navigation from "./components/Navigation/Navigation";
import ImageLinkForm from "./components/ImageLinkForm/ImageLinkForm";
import Rank from "./components/Rank/Rank";
import FaceRecognition from "./components/FaceRecognition/FaceRecognition";

const returnClarifaiRequestOptions = (imageUrl) => {
  // Your PAT (Personal Access Token) can be found in the portal under Authentication
  const PAT = "c998e70a93f04ff291469ab00f1df1ad";
  // Specify the correct user_id/app_id pairings
  // Since you're making inferences outside your app's scope
  const USER_ID = "5hdnsuctgr7b";
  const APP_ID = "test";
  // Change these to whatever model and image URL you want to use
  const IMAGE_URL = imageUrl;

  const raw = JSON.stringify({
    user_app_id: {
      user_id: USER_ID,
      app_id: APP_ID,
    },
    inputs: [
      {
        data: {
          image: {
            url: IMAGE_URL,
          },
        },
      },
    ],
  });

  const requestOptions = {
    method: "POST",
    headers: {
      Accept: "application/json",
      Authorization: "Key " + PAT,
    },
    body: raw,
  };
  return requestOptions;
};

// NOTE: MODEL_VERSION_ID is optional, you can also call prediction with the MODEL_ID only
// https://api.clarifai.com/v2/models/{YOUR_MODEL_ID}/outputs
// this will default to the latest version_id

class App extends Component {
  constructor() {
    super();
    this.state = {
      input: "",
      imageUrl: "",
      box: {}
    };
  }

  calculateFaceLocation = (data) => {
    const clarifaiFace =
      data.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById("inputImage");
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftCol: clarifaiFace.left_col * width,
      topRow: clarifaiFace.top_row * height,
      rightCol: width - (clarifaiFace.right_col * width),
      bottomRow: height - (clarifaiFace.bottom_row * height),
    };
  };

  displayFaceBox = (box) => {
    console.log(box);
    this.setState({ box: box });
  };

  onInputChange = (e) => {
    this.setState({ input: e.target.value });
  };

  onSubmit = async () => {
    try {
      this.setState({ imageUrl: this.state.input });

      const response = await fetch(
        "https://api.clarifai.com/v2/models/face-detection/outputs",
        returnClarifaiRequestOptions(this.state.imageUrl)
      );

      const result = await response.json();
      const faceLocation = this.calculateFaceLocation(result);
      this.displayFaceBox(faceLocation);
    } catch (error) {
      console.log("error", error);
    }
  };

  // onSubmit = () => {
  //   this.setState({ imageUrl: this.state.input });

  //   fetch(
  //     "https://api.clarifai.com/v2/models/face-detection/outputs",
  //     returnClarifaiRequestOptions(this.state.imageUrl)
  //   )
  //     .then((response) => response.json())
  //     .then((result) => this.displayFaceBox(this.calculateFaceLocation(result)))
  //     .catch((error) => console.log("error", error));
  // };
  render() {
    return (
      <div className="App text-center">
        <Navigation />
        <Rank />
        <ImageLinkForm
          submit={this.onSubmit}
          onInputChange={this.onInputChange}
        />
        <FaceRecognition box={this.state.box} mageUrl={this.state.imageUrl} />
      </div>
    );
  }
}

export default App;
